const itemArray=[],getItem=document.getElementById("saveTask"),item=document.getElementById("item").value;function createItem(){const item=document.getElementById("item").value,data={itemvalue:item};itemArray.push(data),localStorage.setItem("item",JSON.stringify(itemArray)),document.getElementById("item").value=""}function displayItems(){const itemsHTML=itemArray.map(item=>`\n                    <div class="item">\n                        <div class="input-controller">\n                        <textarea disabled>${item.itemvalue}</textarea>\n                        <div class="edit-controller">\n                            <i class="fa fa-remove deleteBtn text-danger"></i>\n                            <i class="fa fa-edit editBtn text-info"></i>\n                        </div>\n                        </div>\n                        <div class="update-controller">\n                        <button class="saveBtn">Save</button>\n                        <button class="cancelBtn">Cancel</button>\n                        </div>\n                    </div>\n                    `).join("");document.querySelector("#to-do-list").innerHTML=itemsHTML,activateDeleteListeners(),activateEditListeners(),activateSaveListeners(),activateCancelListeners()}function activateDeleteListeners(){let deleteBtn;document.querySelectorAll(".deleteBtn").forEach((dB,i)=>{dB.addEventListener("click",()=>{deleteItem(i)})})}function deleteItem(i){itemArray.splice(i,1),console.log(itemArray,"mani---"),localStorage.setItem("items",JSON.stringify(itemArray)),displayItems()}function activateEditListeners(){const editBtn=document.querySelectorAll(".editBtn"),updateController=document.querySelectorAll(".update-controller"),inputs=document.querySelectorAll(".input-controller textarea");editBtn.forEach((eB,i)=>{eB.addEventListener("click",()=>{updateController[i].style.display="block",inputs[i].disabled=!1})})}function activateSaveListeners(){const saveBtn=document.querySelectorAll(".saveBtn"),inputs=document.querySelectorAll(".input-controller textarea");saveBtn.forEach((sb,i)=>{sb.addEventListener("click",()=>{updateItems(inputs[i].value,i)})})}function activateCancelListeners(){const cancelBtn=document.querySelectorAll(".cancelBtn"),updateController=document.querySelectorAll(".update-controller"),inputs=document.querySelectorAll(".input-controller textarea");cancelBtn.forEach((cB,i)=>{cB.addEventListener("click",()=>{updateController[i].style.display="none",inputs[i].disabled=!0,inputs[i].style.border="none"})})}function updateItems(text,i){itemArray[i]||(itemArray[i]={}),itemArray[i]=text,console.log(itemArray[i]),localStorage.setItem("item",JSON.stringify(itemArray))}function displayDate(){let showDate=document.getElementById("showDate"),date,formattedDate=(new Date).toLocaleString();showDate.innerHTML=formattedDate,console.log(formattedDate)}getItem.addEventListener("click",e=>{createItem(),displayItems()}),window.onload=function(){displayDate()};